// Iconset API (Home Assistant 0.110 and up):
async function getIcon(name) {
  return { path: LEONS_ICONS_MAP[name.replace(/_/g,'-')] }; //backwards compatible with underscore glyphs
}

async function getIconList() {
  return Object.keys(LEONS_ICONS_MAP).map(icon => ({name: icon}));
}

let leons_icons_mode = 'legacy';

if (!window.frontendVersion || window.frontendVersion <= 20211027.0){
  // new enough to support customIconsets
  leons_icons_mode = 'modern';
  window.customIconsets = window.customIconsets || {};
  window.customIconsets["leon"] = getIcon;
}else{
  // new enough to support getIcon and getIconlist
  leons_icons_mode = 'picker';
  window.customIcons = window.customIcons || {};
  window.customIcons["leon"] = { getIcon, getIconList };
}


console.info(
    `%c HASS-LEONS-ICONS         \n%c Version 2021.12.1 [` + leons_icons_mode + `] `,
    "color: orange; font-weight: bold; background: black",
    "color: white; font-weight: bold; background: dimgray"
);
